
# Funções moeda

# => Aumento 

def aumentar(preco, taxa):
    result =  preco + (preco * taxa / 100)
    return result


# => Diminuindo

def diminuir(preco, taxa):
    result = preco - (preco * taxa / 100)
    return result


#  => função dobro

def dobro(preco):
   result = preco * 2
   return result


#  => função metade

def metade(preco):
   result = preco / 2
   return result





    


